const locationSelect = document.getElementById("location");
const nameInput = document.getElementById("name");
const surnameInput = document.getElementById("surname");
const roleSelect = document.getElementById("role");
const generateButton = document.getElementById("generate");
const savePdfButton = document.getElementById("save-pdf");
const badge = document.querySelector(".badge");



// Controllo campi
function checkForm() {
  if (
    locationSelect.value &&
    nameInput.value.trim() &&
    surnameInput.value.trim() &&
    roleSelect.value
  ) {
    generateButton.disabled = false;
  } else {
    generateButton.disabled = true;
  }
}

locationSelect.addEventListener("change", checkForm);
nameInput.addEventListener("input", checkForm);
surnameInput.addEventListener("input", checkForm);
roleSelect.addEventListener("change", checkForm);

generateButton.addEventListener("click", () => {
  document.getElementById("badge-location").textContent = locationSelect.value;
  document.getElementById("badge-name").textContent = nameInput.value || "Nome";
  document.getElementById("badge-surname").textContent = surnameInput.value || "Cognome";
  document.getElementById("badge-role").textContent = roleSelect.value;
  
  badge.style.display = "flex"; // mostra badge
  savePdfButton.disabled = false;

});



savePdfButton.addEventListener("click", () => {
  const loc = document.getElementById("badge-location").textContent;
  const nome = document.getElementById("badge-name").textContent;
  const cognome = document.getElementById("badge-surname").textContent;
  const ruolo = document.getElementById("badge-role").textContent;
  
  generatePDF(loc, nome, cognome, ruolo);
});

function generatePDF(loc, nome, cognome, ruolo) {
  const { jsPDF } = window.jspdf;
  const pdfWidth = 240.9449 ;
  const pdfHeight = 153.0709 ;

  const pdf = new jsPDF({
    orientation: "landscape",
    unit: "px",
    format: [pdfWidth, pdfHeight]
  });

  const logoBase64 = "data:image/png;base64,";

  // Sfondo bianco
  pdf.setFillColor(255, 255, 255);
  pdf.rect(0, 0, pdfWidth, pdfHeight, "F");

  // Header
  pdf.setFont("helvetica", "bold");
  pdf.setFontSize(12);
  pdf.setTextColor(0, 0, 0);
  pdf.text(["Officina", "OCRA"], 10, 15);
  pdf.text(loc, 110, 15);
  pdf.addImage(logoBase64, "PNG", pdfWidth - 40, 5, 32, 32);

  // Nome
  pdf.setFontSize(24);
  pdf.setTextColor(0, 0, 0);
  pdf.text(nome, 10, 64);

  // Fascia rossa unica
  const redAreaY = 70.05;
  const redAreaHeight = pdfHeight - redAreaY;
  pdf.setFillColor(255, 0, 0);
  pdf.rect(0, redAreaY, pdfWidth, redAreaHeight, "F");

  // Cognome
  pdf.setFontSize(24);
  pdf.setTextColor(255, 255, 255);
  pdf.text(cognome, 10, 88);

  // Ruolo
  pdf.setFontSize(16);
  pdf.text(ruolo, 10, 140);

  // Nome file dinamico
  const fileName = `Badge_${nome}_${cognome}.pdf`;

  // Salva
  pdf.save(fileName);
}

